import React, { useEffect, useState } from 'react';
import axios from 'axios';
function Cart() {
    const [destinations, setDestinations] = useState([]);

    useEffect(() => {
        const fetchCartAndDestinations = async () => {
            try {
                const userID = JSON.parse(localStorage.getItem("userID"));
                const response = await axios.get(`http://localhost:3001/api/getCart/${userID}`);
                const destinationIDs = response.data.cart.items.map(item => item.destinationID);

                if (destinationIDs.length > 0) {
                    const destinationResponse = await axios.post('http://localhost:3001/api/findAllDestinationByIDs', { destinations: destinationIDs });
                    setDestinations(destinationResponse);
                }
                console.log(destinations);
            } catch (error) {
                console.error("Error fetching cart or destinations:", error.message);
            }
        };

        fetchCartAndDestinations();
    }, []);

    return (<div>
        <h1>These are the Items you have added</h1>
        <p>Tick them to proceed</p>
        {
            destinations.data.map((itemCart) => {
                <div>
                    <input type="checkbox" />
                    <h2>{itemCart.title}</h2>
                    <span>{itemCart.description}</span>
                    <img src={itemCart.photos[0]} alt="" />
                    <span>{itemCart.price}</span>
                </div>
            })
        }
    </div>);
}

export default Cart;
