import React, { useState, useEffect } from 'react';
import { Container, Button } from 'react-bootstrap';
import './Profile.css'; // Make sure to import your CSS file
import axios from 'axios';

function Profile() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [userID, setUserID] = useState('');

    useEffect(() => {
        const storedUserID = localStorage.getItem('id');
        const storedUserName = localStorage.getItem('userName');
        const storedEmail = localStorage.getItem('email');
        if (storedUserName) setName(storedUserName);
        if (storedEmail) setEmail(storedEmail);
        if (storedUserID) setUserID(storedUserID);

    }, []);

    const handleNameChange = (event) => setName(event.target.value);
    const handleEmailChange = (event) => setEmail(event.target.value);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = { email, name };
        console.log(formData)
        console.log(userID)
        // try {
        //     const response = await axios.post(`http://localhost:3001/api/updateUserInfor/${userID}`, formData);
        //     console.log(response);
        // } catch (error) {
        //     console.error('Error updating user information', error);
        // }
    };

    return (
        <>
            <h1>Profile Information</h1>
            <Container className="profile-container">
                <div>
                    <img src='/images/cool-profile-picture-minion-13pu7815v42uvrsg.jpg' alt="Profile" className="profile-image" />
                </div>
                <div className="profile-details">
                    <h2>{name}</h2>
                    <form onSubmit={handleSubmit}>
                        <input type="text" value={name} onChange={handleNameChange} placeholder="Name" />
                        <input type="text" value={email} onChange={handleEmailChange} placeholder="Email" />
                        <Button type="submit">Update</Button>
                    </form>
                </div>
            </Container>
        </>
    );
}

export default Profile;
