import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';

function Profile() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    useEffect(() => {
        const storedUserID = localStorage.getItem('userID');
        const storedUserName = localStorage.getItem('userName');
        const storedemail = localStorage.getItem('email');
        if (storedUserName) setName(storedUserName);
        if (storedemail) setName(storedemail);

    }
        , [])



    const [profilePic, setProfilePic] = useState('your-image-url.jpg');
    const handleNameChange = (event) => setName(event.target.value);
    const handleEmailChange = (event) => setEmail(event.target.value);
    const handleProfilePicChange = (event) => setEmail(event.target.value);

    return (
        <Container className="profile-container">
            <div>
                <img src={profilePic} alt="Profile" className="profile-image" />
                <input type="text" value={profilePic} onChange={handleProfilePicChange} placeholder="Image URL" />
            </div>
            <div className="profile-details">
                <h1>{name}</h1>
                <form>
                    <input type="text" value={name} onChange={handleNameChange} placeholder="Name" />
                    <input type="text" value={email} onChange={handleEmailChange} placeholder="Email" />

                </form>
            </div>
        </Container>
    );
}

export default Profile;
