import React from 'react';
import { useState } from 'react';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link } from 'react-router-dom'

function Login() {
    return (
        <Container className="containerLogin text-white p-5 mt-5" >
            <div>
                <h1 className='title '>Login</h1>
                <Image src="./images/Plane.jpg" rounded />
            </div>
            {FormExample()}
        </Container>
    );
}

function FormExample() {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });

    const handleChange = (event) => {

        const { name, value } = event.target;

        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async (event) => {
        console.log(formData)
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/api/login', formData);
            console.log('Response:', response.data);
            // TODO: add the data to the cookie, storage
            // TODO: redirect users
        } catch (error) {
            console.error('Error:', error);
            // TODO: handle error
        }
    };

    return (
        <form className="signupForm" onSubmit={handleSubmit}>
            <fieldset>
                <label htmlFor="email">Email: </label>
                <input type="text" name="email" id="email" onChange={handleChange} />
            </fieldset>
            <fieldset>
                <label htmlFor="password">Password: </label>
                <input type="text" name="password" id="password" onChange={handleChange} />
            </fieldset>
            <Button type='submit'> Sign In </Button>
            <div>Don't have an account yet? <Link to={'/signup'}>Sign Up here</Link> </div>
        </form>
    );
}

export default Login;
